query:
mutation searchParcel ($tracker: TrackerSearchInput!) {
  result: searchParcel (tracker: $tracker) {
    id
    createdAt
    updatedAt
    lastStatus
    lastSyncTracker
    nextSyncTracker
    estimatedDelivery
    store { name picture }
    pudos { type trackingCode }
    trackers { type shippingService trackingCode }
    trackingEvents { trackerType trackingCode createdAt translatedEventId description title to from location { zipcode address locality number complement city state country } additionalInfo }
    pudoEvents { pudoType trackingCode createdAt translatedEventId description status title from to location { zipcode address locality number complement city state country } additionalInfo }
  }
}
